function getVirtulData(year) {
    year = year || '2017';
    var date = +new Date(year + '-01-01');
    var end = +new Date((+year + 1) + '-01-01');
    var dayTime = 3600 * 24 * 1000;
    var data = [];
    for (var time = date; time < end; time += dayTime) {
        data.push([
            time,
            Math.floor(Math.random() * 10000)
        ]);
    }
    return data;
}

